class CompositeTask {
    constructor(name) {
        this.name = name;
        this.tasks = [];
    }

    addTask(task) {
        this.tasks.push(task);
    }

    removeTask(task) {
        const index = this.tasks.indexOf(task);
        if (index !== -1) {
            this.tasks.splice(index, 1);
        }
    }

    getTasks() {
        return this.tasks;
    }

    // Alte metode specifice pentru CompositeTask, cum ar fi metode pentru iterare, căutare etc.
}
export default CompositeTask;
