class TaskListProxy {
    constructor() {
        this.tasks = [];
        this.modifiedTasks = [];
    }

    getTasks() {
        return [...this.tasks, ...this.modifiedTasks];
    }

    addTask(task) {
        this.modifiedTasks.push(task);
    }

    removeTask(indexToRemove) {
        this.modifiedTasks = this.modifiedTasks.filter(
            (taskObject, index) => index !== indexToRemove
        );
    }

    removeAllTasks() {
        this.modifiedTasks = [];
    }

    updateTaskDone(taskIndex, newDone) {
        this.modifiedTasks[taskIndex].done = newDone;
    }

    renameTask(index, newName) {
        this.modifiedTasks[index].name = newName;
    }
}

export default TaskListProxy;
