import React from "react";
import TaskAdapter from "./TaskAdapter";
import "./TaskList.css";

const TaskList = ({ tasks }) => {
    return (
        <ul className="task-list">
            {tasks.map((task) => {
                const adapter = new TaskAdapter(task);
                return (
                    <li key={adapter.getId()} className="task-item">
                        <div className="task-info">
                            <span className="task-name">{adapter.getName()}</span>
                            <span className="task-priority">{adapter.getPriority()}</span>
                        </div>
                        <div className="task-status">
                            {adapter.isDone() ? "Finalizat" : "Nefinalizat"}
                        </div>
                    </li>
                );
            })}
        </ul>
    );
};

export default TaskList;
