import React, { useState } from 'react';

const TaskForm = ({ onAdd }) => {
    const [taskName, setTaskName] = useState('');
    const [taskPriority, setTaskPriority] = useState('urgent'); // Starea pentru prioritatea sarcinii

    const handleTaskNameChange = (event) => {
        setTaskName(event.target.value);
    };

    const handleTaskPriorityChange = (event) => {
        setTaskPriority(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (taskName.trim() === '') {
            return;
        }
        onAdd(taskName, taskPriority);
        setTaskName('');
        setTaskPriority('urgent');
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" value={taskName} onChange={handleTaskNameChange} placeholder="Nume sarcină" />
            <select value={taskPriority} onChange={handleTaskPriorityChange}>
                <option value="urgent">Urgent</option>
                <option value="mediu">Mediu</option>
                <option value="scazut">Scăzut</option>
            </select>
            <button type="submit">Adaugă sarcină</button>
        </form>
    );
};

export default TaskForm;
